import { data } from '../../data/data';

export default function Configuration() {

    let joueurs = data.enfants;
    let nbJoueursChoisi = 0;

    const choisirNombreJoueur = (nbJoueur) => {
        nbJoueursChoisi = nbJoueur; 
    }

    return (
        <>
            <div>
                Configuration
                <p>Combien de personnes jouent ? </p>
              <div>
                <button onClick={() => choisirNombreJoueur(2)} >2</button>
                <button onClick={() => choisirNombreJoueur(3)} >3</button>
                <button onClick={() => choisirNombreJoueur(4)} >4</button>
              </div>

              {joueurs.map(enfant => enfant.nom)}

            </div>
        </>
    )
}
