import { data } from '../../data/data';

export default function Configuration() {

    let listJoueurs = data.enfants;
    let listAnimateurs = data.animateurs;
    let nbJoueursChoisi = 0;
    let tabJoueursChoisis = [];

    const choisirNombreJoueur = (nbJoueur) => {
        console.log('avant : '+nbJoueursChoisi);
        nbJoueursChoisi = nbJoueur; 
        console.log('après : '+nbJoueursChoisi);
    }

    const choixJoueur = (joueur) => {
        console.log('joueurs choisis : '+tabJoueursChoisis.length);
        console.log('nombre joueurs total : '+nbJoueursChoisi);
        if (tabJoueursChoisis.length < (nbJoueursChoisi-1)) {
            console.log('joueur : '+joueur);
            tabJoueursChoisis.push(joueur);
        }
        else {
            console.log('cannot add more players');
        }
        console.log(tabJoueursChoisis);
    }

    return (
        <>
            <div>
                Configuration
                <p>Combien de personnes jouent ? </p>
              <div>
                <button onClick={() => choisirNombreJoueur(2)} >2</button>
                <button onClick={() => choisirNombreJoueur(3)} >3</button>
                <button onClick={() => choisirNombreJoueur(4)} >4</button>
              </div>

              <div id='listJoueursEnfants'>
                    {listJoueurs.map(enfant => <div className='enfantInfos' onClick={() => choixJoueur({enfant})} >{enfant.nom}</div>)}
              </div>

              <div id='listAnimateurs'>
              </div>

            </div>
        </>
    )
}
